function BFTValidate(frm) {
	var email = frm.email.value;
	if(email=="" || email.indexOf("@")<1 ||email.indexOf(".")<1) {
		alert("Wpisz poprawny email!");
		frm.email.focus();
		return false;
	}
	return true;
};

